import React from "react";
import PostItem from "./PostItem";

const PostList = ({ add, remove, doneposts, posts, create, flag }) => {
    return (
        <div style={{display:"flex"}}>
            <div style={{width:400, marginRight:20}}>
            { 
                flag === true && posts.length > 0 ?
                posts.map((post) => <PostItem set={add} remove={remove} 
                donePosts = {doneposts} posts={posts} post={post} create={create} flag={flag}/>) 
                : 
                flag === false && doneposts.length > 0 ?
                doneposts.map((post) => <PostItem set={add} remove={remove} 
                donePosts = {doneposts} posts={posts} post={post} create={create} flag={flag}/>) : <></>
            }
            </div>
        </div>
        
    );
};

export default PostList;
