import React, { useState } from 'react';
import { useEffect } from 'react';
import PostList from './components/PostList';
import PostForm from './components/PostForm';
import Login from './components/Login';
import MyButton from './components/UI/button/mybutton.jsx';
import "./css/bootstrap.min.css"
import "./styles/app.css"

function App() {

    const DragAndDrop = () => {
        const [isDragging, setIsDragging] = useState(false);

        const handleDragStart = (e) => {
            setIsDragging(true);
            e.dataTransfer.setData('text/plain', 'Hello'); // Передаем произвольные данные
        };

        const handleDragEnd = () => {
            setIsDragging(false);
        };

        const handleDrop = (e) => {
            e.preventDefault();
            setIsDragging(false);

            const data = e.dataTransfer.getData('text/plain');
            console.log('Dropped data:', data);
            // Вы можете обработать данные, которые были брошены, здесь.
        };

        const handleDragOver = (e) => {
            e.preventDefault();
        };

        const [posts, setPosts] = useState([]);
        const [donePosts, setDonePosts] = useState([]);

        const [user, setUser] = useState({ login: '', password: "" });
        const [islogin, setLogin] = useState(JSON.parse(localStorage.getItem('boollog:')) | false);
        const [isPopupOpen, setPopupOpen] = useState(false);

        useEffect(() => {
            setUser(JSON.parse(localStorage.getItem('lastuser:')));
            const stringuser = localStorage.getItem('lastuser:');
            if (stringuser) {
                const user = JSON.parse(stringuser);
                getPosts(user);
            }
        }, []);

        const getPosts = (returneduser) => {
            let SavedPosts = null;
            if (returneduser)
                SavedPosts = localStorage.getItem(JSON.stringify(returneduser)); // берем элемент из стораж
            if (SavedPosts) {
                const PostsArray = JSON.parse(SavedPosts);
                var tempdonePosts = [];
                var tempposts = [];
                for (let i = 0; i < PostsArray.length; i++) {
                    if (PostsArray[i].completed)
                        tempdonePosts.push(PostsArray[i]);
                    else
                        tempposts.push(PostsArray[i]);
                }
                setPosts(tempposts); // парсим в строку все объекты
                setDonePosts(tempdonePosts)
            }
            else {
                setPosts([]);
                setDonePosts([]);
            }
        }

        const savePosts = (posts) => {
            if (user)
                localStorage.setItem(JSON.stringify(user), JSON.stringify(posts)); // кладем элемент в стораж
        }

        const createPost = (newPost, posts, antiPosts, flag) => {
            if (newPost.title !== '') {
                if (newPost.completed === false)
                    setPosts([...posts, newPost]) // задаем все элементы
                else
                    setDonePosts([...posts, newPost])
            }
            if (flag)
                savePosts([...posts, newPost]);
            else
                savePosts([...posts, ...antiPosts]); // сохраняем все и последний
        }

        const removePost = (post, curPosts, antiPosts) => {
            if (post.completed === true)
                setDonePosts(curPosts.filter(p => p.id !== post.id)); // задаем
            else
                setPosts(curPosts.filter(p => p.id !== post.id)); // задаем
            savePosts(antiPosts.concat(curPosts.filter(p => p.id !== post.id))); // сохраняем
        }

        const WrongPassword = () => {
            alert('Неправильный пароль');
        }

        return (
            <div class="container">
                <div class="row">
                    <div
                        onDragStart={handleDragStart}
                        onDragEnd={handleDragEnd}
                        onDrop={handleDrop}
                        onDragOver={handleDragOver}
                        draggable
                        style={{ border: isDragging ? '2px dashed red' : '2px solid black', padding: '10px' }}
                    >
                        Drag me!
                    </div>
                    {
                        !isPopupOpen
                            ? <MyButton onClick={() => setPopupOpen(true)}>Открыть окно регистрации</MyButton>
                            : <MyButton onClick={() => setPopupOpen(false)}>Закрыть окно регистрации</MyButton>
                    }

                    <Login L={islogin} SL={setLogin} AccessAllowed={getPosts} AccessRestricted={WrongPassword}
                        onLogin={(user) => setUser(user)} isOpen={isPopupOpen} onClose={() => setPopupOpen(false)} />
                    <PostForm create={createPost} posts={posts} antiPosts={donePosts} />

                    <h1 style={{ fontFamily: 'cursive', textAlign: 'center' }}>Список дел</h1>

                    <div class="col-6">
                        {
            /*?*/   posts.length !== 0 ?
                                <PostList add={setDonePosts} remove={removePost} create={createPost}
                                    doneposts={donePosts} posts={posts} flag={true} /> : <></>
                        }
                    </div>

                    <div class="col-6">
                        {
            /*?*/   donePosts.length !== 0 ?
                                <PostList add={setPosts} remove={removePost} create={createPost}
                                    doneposts={donePosts} posts={posts} flag={false} /> : <></>
                        }
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
