import React, { useState } from 'react';
import { useEffect } from 'react';
import "./styles/app.css"
import PostList from './components/PostList';
import PostForm from './components/PostForm';
import Login from './components/Login';

function App() {

    const [posts, setPosts] = useState([]);
    const [user, setUser] = useState({ login: '', password: "" });
    const [islogin, setLogin] = useState(JSON.parse(localStorage.getItem('boollog:')) | false);
    const [isPopupOpen, setPopupOpen] = useState(false);
    const [openLogin, setOpenLogin] = useState(false);

    useEffect(() => {
        setUser(JSON.parse(localStorage.getItem('lastuser:')));
        const stringuser = localStorage.getItem('lastuser:');
        if (stringuser) {
            const user = JSON.parse(stringuser);
            getPosts(user);
        }
    }, []);

    const getPosts = (returneduser) => {
        let SavedPosts = null;
        if (returneduser)
            SavedPosts = localStorage.getItem(JSON.stringify(returneduser)); // берем элемент из стораж
        if (SavedPosts)
            setPosts(JSON.parse(SavedPosts)); // парсим в строку все объекты
        else
            setPosts([]);
    }

    const savePosts = (posts) => {
        if (user)
            localStorage.setItem(JSON.stringify(user), JSON.stringify(posts)); // кладем элемент в стораж
    }

    const createPost = (newPost) => {
        if (newPost.title !== '' && newPost.body !== '') {
            setPosts([...posts, newPost]) // задаем все элементы
            savePosts([...posts, newPost]); // сохраняем все и последний
        }
        console.log(posts);
    }

    const removePost = (post) => {
        setPosts(posts.filter(p => p.id !== post.id)); // задаем
        savePosts(posts.filter(p => p.id !== post.id)); // сохраняем
        console.log(posts);
    }

    const WrongPassword = () => {
        alert('Неправильный пароль');
    }

    const openPopup = () => {
        setPopupOpen(true);
    };

    const closePopup = () => {
        setPopupOpen(false);
    };

    return (
        <div className="App">
            {
                !openLogin
                    ? <button onClick={openPopup}>Открыть окно регистрации</button>
                    : <button onClick={() => setOpenLogin(false)}>Закрыть окно регистрации</button>
            }

            <Login L={islogin} SL={setLogin} AccessAllowed={getPosts} AccessRestricted={WrongPassword}
                onLogin={(user) => setUser(user)} isOpen={isPopupOpen} onClose={closePopup} />
            <PostForm create={createPost} />
            {
                posts.length !== 0
                    ?
                    <PostList add={setPosts} remove={removePost} posts={posts} title="Список дел" />
                    :
                    <h1 style={{ fontFamily: 'cursive', textAlign: 'center' }}>
                        Дел нет
                    </h1>
            }
        </div>
    );
}

export default App;
