import React, { useState } from 'react';
import { useEffect } from 'react';
import { Button, ButtonGroup } from 'react-bootstrap';
import PostList from './components/PostList';
import PostForm from './components/PostForm';
import Login from './components/Login';
import "./css/bootstrap.min.css";
import "./styles/app.css";

function App() {

    const handleDrop = (e, targetColumn) => {
        e.preventDefault();
        const draggedItemId = +e.dataTransfer.getData('text/plain');
        var draggedItem;
        if (targetColumn == 'done')
            draggedItem = posts.find((item) => item.id === draggedItemId);
        else
            draggedItem = donePosts.find((item) => item.id === draggedItemId);


        if (targetColumn === 'done' && draggedItem) {
            setDonePosts([...donePosts, draggedItem]);
            setPosts(posts.filter((item) => item.id !== draggedItemId));
        }
        else if (targetColumn === 'todo' && draggedItem) {
            setPosts([...posts, draggedItem]);
            setDonePosts(donePosts.filter((item) => item.id !== draggedItemId));
        }
        if (draggedItem)
            draggedItem.completed = !draggedItem.completed;

        savePosts([...posts, ...donePosts]);
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const [posts, setPosts] = useState([]);
    const [donePosts, setDonePosts] = useState([]);

    const [user, setUser] = useState({ login: '', password: "" });
    const [islogin, setLogin] = useState(JSON.parse(localStorage.getItem('boollog:')) | false);
    const [isPopupOpen, setPopupOpen] = useState(false);

    useEffect(() => {
        setUser(JSON.parse(localStorage.getItem('lastuser:')));
        const stringuser = localStorage.getItem('lastuser:');
        if (stringuser) {
            const user = JSON.parse(stringuser);
            getPosts(user);
        }
    }, []);

    const getPosts = (returneduser) => {
        let SavedPosts = null;
        if (returneduser)
            SavedPosts = localStorage.getItem(JSON.stringify(returneduser)); // берем элемент из стораж
        if (SavedPosts) {
            const PostsArray = JSON.parse(SavedPosts);
            var tempdonePosts = [];
            var tempposts = [];
            for (let i = 0; i < PostsArray.length; i++) {
                if (PostsArray[i].completed)
                    tempdonePosts.push(PostsArray[i]);
                else
                    tempposts.push(PostsArray[i]);
            }
            setPosts(tempposts); // парсим в строку все объекты
            setDonePosts(tempdonePosts)
        }
        else {
            setPosts([]);
            setDonePosts([]);
        }
    }

    const savePosts = (posts) => {
        if (user)
            localStorage.setItem(JSON.stringify(user), JSON.stringify(posts)); // кладем элемент в стораж
    }

    const createPost = (newPost, posts, antiPosts, flag) => {
        if (newPost.title !== '') {
            if (newPost.completed === false)
                setPosts([...posts, newPost]); // задаем все элементы
            else
                setDonePosts([...posts, newPost]);
        }
        if (flag)
            savePosts([...posts, newPost]);
        else
            savePosts([...posts, ...antiPosts]); // сохраняем все и последний
    }

    const removePost = (post, curPosts, antiPosts) => {
        if (post.completed === true)
            setDonePosts(curPosts.filter(p => p.id !== post.id)); // задаем
        else
            setPosts(curPosts.filter(p => p.id !== post.id)); // задаем
        savePosts(antiPosts.concat(curPosts.filter(p => p.id !== post.id))); // сохраняем
    }

    const WrongPassword = () => {
        alert('Неправильный пароль');
    }

    return (
        <div class="container">
            <div class="row" style={{ width: '100%' }}>
                {
                    !isPopupOpen ?
                        <button
                            type='button'
                            style={{
                                width: 110,
                                margin: "10px 0 10px 0",
                                height: 60
                            }}
                            onClick={() => setPopupOpen(true)}
                            class="btn btn-primary" 
                            data-bs-toggle="modal" 
                            data-bs-target="#exampleModal"
                            >Войти</button>
                        : <button
                            type='button'
                            style={{
                                width: 110,
                                margin: '10px 0 10px 0',
                                height: 60
                            }}
                            onClick={() => setPopupOpen(false)}
                            class="btn btn-primary" 
                            data-bs-toggle="modal" 
                            data-bs-target="#exampleModal"
                            >Войти</button>
                }

                <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div class="modal-dialog">
                        <div class="modal-content">
                            <div class="modal-header">
                                <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
                                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                            </div>
                            <div class="modal-body">
                                <Login L={islogin} SL={setLogin} AccessAllowed={getPosts} AccessRestricted={WrongPassword}
                                    onLogin={(user) => setUser(user)} isOpen={isPopupOpen} onClose={() => setPopupOpen(false)} />
                            </div>
                            <div class="modal-footer">
                                <button type="button" class="btn btn-primary">Вход</button>
                            </div>
                        </div>
                    </div>
                </div>

                <PostForm create={createPost} posts={posts} antiPosts={donePosts} />

                <div className="col-6"
                    onDrop={(e) => handleDrop(e, 'todo')}
                    onDragOver={handleDragOver}
                    style={{
                        backgroundColor: 'rgb(159, 255, 140)',
                        border: '2px solid #0d6efd',
                        padding: '10px',
                        margin: '0px',
                        flex: 1
                    }}>
                    <h1 style={{ backgroundColor: 'rgb(159, 255, 140)' }}>Todo</h1>
                    {
            /*?*/   posts.length !== 0 ?
                            <PostList add={setDonePosts} remove={removePost} create={createPost}
                                doneposts={donePosts} posts={posts} flag={true} /> : <></>
                    }
                </div>

                <div className="col-6"
                    onDrop={(e) => handleDrop(e, 'done')}
                    onDragOver={handleDragOver}
                    style={{
                        backgroundColor: 'rgb(228, 57, 57)',
                        border: '2px solid #0d6efd',
                        padding: '10px',
                        margin: '0px',
                        flex: 1
                    }}>
                    <h1 style={{ backgroundColor: 'rgb(228, 57, 57)' }}>Done</h1>
                    {
            /*?*/   donePosts.length !== 0 ?
                            <PostList add={setPosts} remove={removePost} create={createPost}
                                doneposts={donePosts} posts={posts} flag={false} /> : <></>
                    }
                </div>
            </div>
        </div>
    );
}

export default App;
