import React, { useState } from 'react';
import { useEffect } from 'react';
import "./styles/app.css"
import PostList from './components/PostList';
import PostForm from './components/PosForm';
// import MyInput from './components/UI/input/myinput';
// import classes from './styles/app.css'
// import MyButton from './components/UI/button/mybutton';

function App() {

    const [posts, setPosts] = useState([]);

    useEffect(() => {
        const SavedPosts = localStorage.getItem('qq'); // берем элемент из стораж

        if (SavedPosts)
            setPosts(JSON.parse(SavedPosts)); // парсим в строку все объекты
        
    }, []); // пустой массив, значит вызовется один раз

    const savePosts = (posts) => {
        localStorage.setItem('qq', JSON.stringify(posts)); // кладем элемент в стораж
    }

    const createPost = (newPost) => {
        if (newPost.title !== '' && newPost.body !== '') { 
            setPosts([...posts, newPost]) // задаем все элементы
            savePosts([...posts, newPost]); // сохраняем все и последний
        }

    }

    const removePost = (post) => {
        setPosts(posts.filter(p => p.id !== post.id)); // задаем
        savePosts(posts.filter(p => p.id !== post.id)); // сохраняем
    }

    return (
        <div className="App">
           {/* <div className="login">
                <h3>Вход и регистрация</h3>
                <MyInput 
                    className={classes.loginInput}
                    type="text"
                    placeholder='Имя пользователя'
                 />
                <MyInput 
                    className={classes.loginInput}
                    type="text"
                    placeholder='Пароль'
                 />
                <MyButton 
                className={classes.loginButton}>
                    Вход
                </MyButton>
            </div> */}
            <PostForm create={createPost} />
            {posts.length !== 0
                ?
                <PostList posts={posts} title="Список дел" remove={removePost} />
                :
                <h1 style={{ fontFamily: 'sans-serif', textAlign: 'center' }}>
                    Дел нет
                </h1>
            }
        </div>
    );
}

export default App;
