import React, { useState } from 'react';
import { useEffect } from 'react';
import "./styles/app.css"
import PostList from './components/PostList';
import PostForm from './components/PostForm';
import Login from './components/Login';

function App() {

    const [posts, setPosts] = useState([]);
    let User;

    // useEffect(() => {
    //    // const SavedPosts = localStorage.getItem('qq'); // берем элемент из стораж

    //   //  if (SavedPosts)
    //    //     setPosts(JSON.parse(SavedPosts)); // парсим в строку все объекты

    // }, []); // пустой массив, значит вызовется один раз

    const savePosts = (posts) => {
        if(User)
            localStorage.setItem(User.login, JSON.stringify(posts)); // кладем элемент в стораж
    }

    const getPosts = (User) => {
        let SavedPosts;
        if(User)
            SavedPosts = localStorage.getItem(User.login); // кладем элемент в стораж
        else
            SavedPosts = localStorage.getItem('qq');

        if (SavedPosts)
            setPosts(JSON.parse(SavedPosts)); // парсим в строку все объекты
    }

    const createPost = (newPost) => {
        if (newPost.title !== '' && newPost.body !== '') { 
            setPosts([...posts, newPost]) // задаем все элементы
            savePosts([...posts, newPost]); // сохраняем все и последний
        }

    }

    const removePost = (post) => {
        setPosts(posts.filter(p => p.id !== post.id)); // задаем
        savePosts(posts.filter(p => p.id !== post.id)); // сохраняем
    }

    const LoadData = (user) => {
        getPosts(user);
    }

    const WrongPassword = () => {

    }

    return (
        <div className="App">
            { 
            <Login AccessAllowed={LoadData} AccessRestricted={WrongPassword}/>
            }
            <PostForm create={createPost} />
            {
                posts.length !== 0
                ?
                <PostList posts={posts} title="Список дел" remove={removePost} />
                :
                <h1 style={{ fontFamily: 'sans-serif', textAlign: 'center' }}>
                    Дел нет
                </h1>
            }
        </div>
    );
}

export default App;
