import React, { useState } from "react";
import { useEffect } from "react";
import classes from "../styles/Login.module.css"
import { Button } from 'react-bootstrap';
import { InputGroup } from "react-bootstrap";

const Login = ({ L, SL, AccessAllowed, AccessRestricted, onLogin, isOpen, onClose }) => {

    const [userData, SetuserData] = useState({ login: '', password: '' });

    useEffect(() => {
        SetuserData(JSON.parse(localStorage.getItem('lastuser:')));
    }, [])

    useEffect(() => {
        localStorage.setItem('boollog:', JSON.stringify(L));
    }, [L]);

    if(!isOpen)
        return null;

    const HandleLogin = (UserData) => {
        let flag = 0;
        let ValidLogin = 0;

        if (UserData.login.length < 5)
            alert("Логин должен быть не меньше 5 символов");
        else if (UserData.password.length < 8)
            alert("Пароль должен быть не меньше 8 символов")
        else
            ValidLogin = 1;

        for (let i = 0; i < localStorage.length && ValidLogin; i++) {
            const key = localStorage.key(i); // Получаем ключ очередного элемента

            if (key.startsWith('{"login":')) {
                try {
                    const userData = JSON.parse(key);
                    if (userData.login === UserData.login) {
                        if (userData.password === UserData.password) {
                            SL(true);

                            onLogin(userData);
                            AccessAllowed(userData);

                            localStorage.setItem('lastuser:', JSON.stringify(UserData));
                            onClose();
                        }
                        else
                            AccessRestricted();
                        flag = 1;
                    }
                }
                catch (error) {
                    console.error('Ошибка при разборе данных:', error);
                }
            }
        }
        if (!flag && ValidLogin) {
            SL(true);

            onLogin(userData);
            AccessAllowed(userData);

            localStorage.setItem('lastuser:', JSON.stringify(UserData));
            localStorage.setItem(JSON.stringify(userData), []);

            onClose();
        }
    }

    const LogOut = () => {
        SL(false);
        SetuserData({ login: '', password: '' }); // меняем на дефолтного

        localStorage.setItem('lastuser:', JSON.stringify({ login: '', password: '' }));
        localStorage.setItem(JSON.stringify({ login: '', password: '' }), []); // удаляем посты

        onLogin({ login: '', password: '' });
        AccessAllowed({ login: '', password: '' });
    }

    return (
        <div className={classes.login}>
            <h3 style={{
                fontFamily: 'cursive',
                textAlign: 'center',
                marginBottom: 10,
                fontSize: 24
            }}>Вход и регистрация
            </h3>
            <InputGroup 
               // class="form-control" 
                className={classes.InputLogin}
                disabled={L}
                type="text"
                placeholder='Имя пользователя'
                value={userData.login}
                onChange={e => SetuserData({ ...userData, login: e.target.value })}
            />
            <InputGroup
               // class="form-control" 
                className={classes.InputLogin}
                disabled={L}
                type="text"
                placeholder='Пароль'
                value={userData.password}
                onChange={e => SetuserData({ ...userData, password: e.target.value })}
            />
            {   
                L ?
                    <Button onClick={() => { LogOut() }}>Выход</Button>
                    :
                    <Button onClick={() => { HandleLogin(userData) }}>Вход</Button>
            }
        </div>
    )
}

export default Login
