import React, { useState } from 'react';
import {useEffect} from 'react';
import "./styles/app.css"
import PostItem from './components/Postitem';
import PostList from './components/PostList';
import MyButton from './components/UI/button/mybutton';
import MyInput from './components/UI/input/myinput';
import PostForm from './components/PosForm';

function App() {

    const [posts, setPosts] = useState([]);

    useEffect(() => {
        const SavedPosts = localStorage.getItem('qq');

        if(SavedPosts) {
            setPosts(JSON.parse(SavedPosts));
        }
    }, []);
    
    const savePosts = (posts) => {
        localStorage.setItem('qq', JSON.stringify(posts));
    }

    const createPost = (newPost) => {
        if (newPost.title !== '' && newPost.body !== '') {
            setPosts([...posts, newPost])
            savePosts([...posts, newPost]);
        }
           
    }

    const removePost = (post) => {
        setPosts(posts.filter(p => p.id !== post.id));
        savePosts(posts.filter(p => p.id !== post.id));
    } 

    return (
        <div className="App">
            <PostForm create={createPost}/>
            {posts.length !== 0
                ?
                <PostList remove ={removePost} posts={posts} title="Список дел"/>
                :
                <h1 style = {{textAlign: 'center'}} >
                    Дел нет
                </h1>
            }
        </div>
    );
}

export default App;
