import React, { useState } from 'react';
import { useEffect } from 'react';
import "./styles/app.css"
import PostList from './components/PostList';
import PostForm from './components/PostForm';
import Login from './components/Login';

function App() {

    const [posts, setPosts] = useState([]);
    const [user, setUser] = useState(null);
    //const [userInitial, setInitial] = useState({login: '', password: ''});

    useEffect(() => {
        for (let i = 0; i < localStorage.length; i++) {
            const key = localStorage.key(i); // Получаем ключ (имя) текущего элемента

            if(key.startsWith('lastuser:')) {
                const stringuser = localStorage.getItem(key);
                const user = JSON.parse(stringuser);
                getPosts(user);
                setUser(user);
                console.log(user);
                break;
            }
        }
    }, []);

    const getPosts = (user) => {
        var SavedPosts = null;
        if(user)
            SavedPosts = localStorage.getItem(JSON.stringify(user)); // берем элемент из стораж
        if (SavedPosts)
            setPosts(JSON.parse(SavedPosts)); // парсим в строку все объекты
        else 
            setPosts([]);
    }

    const savePosts = (posts) => {
        if(user) 
            localStorage.setItem(JSON.stringify(user), JSON.stringify(posts)); // кладем элемент в стораж
    }

    const createPost = (newPost) => {
        if (newPost.title !== '' && newPost.body !== '') { 
            setPosts([...posts, newPost]) // задаем все элементы
            savePosts([...posts, newPost]); // сохраняем все и последний
        }
    }

    const removePost = (post) => {
        setPosts(posts.filter(p => p.id !== post.id)); // задаем
        savePosts(posts.filter(p => p.id !== post.id)); // сохраняем
    }

    const WrongPassword = () => {
        alert('Неправильный пароль');
    }

    return (
        <div className="App">
            { 
                <Login InitUser={user} AccessAllowed={getPosts} AccessRestricted={WrongPassword} onLogin={(user) => setUser(user)}/>
            }
            <PostForm create={createPost} />
            {
                posts.length !== 0
                ?
                <PostList posts={posts} title="Список дел" remove={removePost} />
                :
                <h1 style={{ fontFamily: 'sans-serif', textAlign: 'center' }}>
                    Дел нет
                </h1>
            }
        </div>
    );
}

export default App;
