import React, { useState } from "react";
import InputLogin from "./UI/login/inputlogin";
import MyButton from "./UI/button/mybutton";
import classes from "./UI/login/InputLogin.module.css"

const Login = ({ InitUser, AccessAllowed, AccessRestricted, onLogin }) => {

    const [userData, SetuserData] = useState({login: '', password: ''})

    const HandleLogin = (UserData) => {
        var flag = 0;
        // Пройдем по всем элементам localStorage
        for (let i = 0; i < localStorage.length; i++) {
            const key = localStorage.key(i); // Получаем ключ (имя) текущего элемента

            if (key.startsWith('{"login":')) {
                try {
                    const userData = JSON.parse(key);
                    if (userData.login === UserData.login) {
                        if (userData.password === UserData.password) {
                            AccessAllowed(userData);
                            onLogin(userData);
                            localStorage.setItem('lastuser:', JSON.stringify(UserData));
                        }
                        else  {
                            AccessRestricted();

                        }
                        flag = 1;
                    }
                } 
                catch (error) {
                    console.error('Ошибка при разборе данных:', error);
                }
            }
        }
        if(!flag){
            localStorage.setItem(JSON.stringify(userData), []);
            AccessAllowed(userData);
            onLogin(userData);
        }
    }

    const LogOut = () => {

    }

    return (
        <div className={classes.login}>
            <h3>Вход и регистрация</h3>
            <InputLogin
                type="text"
                placeholder='Имя пользователя'
                value={userData.username}
                onChange={e => SetuserData({ ...userData, login: e.target.value })}
            />
            <InputLogin 
                type="text"
                placeholder='Пароль'
                value={userData.password}
                onChange={e => SetuserData({ ...userData, password: e.target.value })}
            />
            <MyButton onClick={() => {HandleLogin(userData)}}>Вход</MyButton>
            <MyButton onClick={() => {LogOut()}}>Выход</MyButton>
        </div>
    )
}

export default Login
