import React from "react";
import {useState} from "react";
import { useEffect } from "react";
import ButClass from "../styles/MyButton.module.css";

const PostItem = (props) => {

    const [completed, setCompleted] = useState(false);

    useEffect(() => {
        for (let j = 0; j < props.posts.length; j++)
            if (props.posts[j].id === props.post.id)
                setCompleted(props.posts[j].completed);
    }, [props.posts])

    const handleCheckbox = () => {
        setCompleted(!completed);

        let key;
        let postsArray;

        for(let i = 0; i < localStorage.length; i++) {
            key = localStorage.key(i);
            const value = localStorage.getItem(key);

            if (key.startsWith('{"login":')) {
                try {
                    postsArray = JSON.parse(value);
                    for(let j = 0; j < postsArray.length; j++)
                        if(props.post.id === postsArray[j].id) {
                            props.posts[j].completed = !props.posts[j].completed;
                            props.set(props.posts);
                            localStorage.setItem(JSON.stringify(JSON.parse(key)), JSON.stringify(props.posts));
                        }
                }
                catch (error) {
                    console.error('Ошибка при разборе данных:', error);
                }
            }
        }
    }

    return (
        <div className="post">
            <input type="checkbox" className="chb" onChange={handleCheckbox} checked={completed}/>
            <div className="post__content">
                <strong style={{fontSize:20, fontFamily:'cursive'}}>{props.post.title}</strong>
                <div style={{fontSize:15, fontFamily:'cursive'}}>
                    {props.post.body}
                </div>
            </div>
            <div>
                <button className={ButClass.myBtn} onClick={() => props.remove(props.post)}>
                    Удалить
                </button>
            </div>
        </div>
    )
}

export default PostItem
