// DropContainer.jsx
import { useDrop } from 'react-dnd';

const DropContainer = ({ onDrop }) => {
  const [, drop] = useDrop({
    accept: 'ITEM',
    drop: (item) => onDrop(item),
  });

  return <div ref={drop}>Перетащите сюда элементы</div>;
};

export default DropContainer;
