import React from "react";
import PostItem from "./PostItem";

const PostList = ({ add, remove, posts, title }) => {
    return (
        <div style ={{}}>
            <h1 style={{fontFamily: 'cursive', textAlign: 'center'}}>
                {title}
            </h1>
            {posts.map((post) => // применение функции для всех элементов
                <PostItem set={add} remove={remove} posts={posts} post={post} />
            )}
        </div>
    );
};

export default PostList;
