        savePosts(posts.filter(p => p.id !== post.id)); // сохраняем
        setCompleted(!completed);
        setPosts([...posts, newPost]) // задаем все элементы
        savePosts([...posts, newPost]); // сохраняем все и последний
        props.post.completed = completed;
    }

    return (
        <div className="post">
            <input type="checkbox" className="chb" onClick={handleCheckbox} checked={completed}/>
            <div className="post__content">
                <strong style={{fontSize:20, fontFamily:'cursive'}}>{props.post.title}</strong>
                <div style={{fontSize:15, fontFamily:'cursive'}}>
                    {props.post.body}
                </div>
            </div>
            <div>
                <button className={ButClass.myBtn} onClick={() => props.remove(props.post)}>
                    Удалить
                </button>
            </div>
        </div>
    )
}

export default PostItem
