import React, { useState } from "react";
import InputLogin from "./UI/login/inputlogin";
import MyButton from "./UI/button/mybutton";
import classes from "./UI/login/InputLogin.module.css"
import { useEffect } from "react";

const Login = ({ L, SL, AccessAllowed, AccessRestricted, onLogin }) => {

    const lus = localStorage.getItem('lastuser:');
    const parstedlus = JSON.parse(lus);
    stringparstedlis = localStorage.getItem(JSON.stringify(parstedlus))

    const [userData, SetuserData] = useState( {login: '', password: ''});

    useEffect (()=> {
        localStorage.setItem('boollog:', JSON.stringify(L));
    }, [L]);

    // useEffect (() => {

    //     SetuserData({
    //         ...userData,
    //         login: "342",
    //         password: "123",
    //     })
    // }, [])

    const HandleLogin = (UserData) => {
        var flag = 0;
        var ValidLogin = 0;

        if(userData.login < 5)
            alert("Логин должен быть не меньше 5 символов");
        else if(userData.password < 8)
            alert("Пароль должен быть не меньше 8 символов")
        else
            ValidLogin = 1;

        // Пройдем по всем элементам localStorage
        for (let i = 0; i < localStorage.length && ValidLogin; i++) {
            const key = localStorage.key(i); // Получаем ключ (имя) текущего элемента

            if (key.startsWith('{"login":')) {
                try {
                    const userData = JSON.parse(key);
                    if (userData.login === UserData.login) {
                        if (userData.password === UserData.password) {
                            SL(true);

                            AccessAllowed(userData);
                            onLogin(userData);
                            localStorage.setItem('lastuser:', JSON.stringify(UserData));
                        }
                        else  {
                            AccessRestricted();
                        }
                        flag = 1;
                    }
                } 
                catch (error) {
                    console.error('Ошибка при разборе данных:', error);
                }
            }
        }
        if(!flag && ValidLogin){
            SL(true);

            AccessAllowed(userData);
            onLogin(userData);
            localStorage.setItem(JSON.stringify(userData), []);
        }
    }

    const LogOut = () => {
        SL(false);

        AccessAllowed({login: '', password: ''});
        onLogin({login:'', password: ''});
        localStorage.setItem('lastuser:', JSON.stringify({login: '', password: ''}));
        localStorage.setItem(JSON.stringify({login: '', password: ''}), []);
    }

    return (
        <div className={classes.login}>
            <h3>Вход и регистрация</h3>
            <InputLogin
                disabled={L}
                type="text"
                placeholder='Имя пользователя'
                value={userData.login}
                onChange={e => SetuserData({ ...userData, login: e.target.value })}
            />
            <InputLogin 
                disabled={L}
                type="text"
                placeholder='Пароль'
                value={userData.password}
                onChange={e => SetuserData({ ...userData, password: e.target.value })}
            />
            {
                L ? 
                <MyButton onClick={() => {LogOut()} }>Выход</MyButton>
                :
                <MyButton onClick={() => {HandleLogin(userData)}}>Вход</MyButton>    
            }
        </div>
    )
}

export default Login
