import React, { useState } from 'react';
import { useEffect } from 'react';
import "./styles/app.css"
import PostList from './components/PostList';
import PostForm from './components/PostForm';
import Login from './components/Login';

function App() {

    const [posts, setPosts] = useState([]);
    const [user, setUser] = useState(null);

    // useEffect(() => {
    //     const userKey = `userData_${User.login}`;
    //     const SavedPosts = localStorage.getItem(userKey); // берем элемент из стораж

    //     if (SavedPosts)
    //         setPosts(JSON.parse(SavedPosts)); // парсим в строку все объекты

    // }, []); // пустой массив, значит вызовется один раз

    const getPosts = (user) => {
        var SavedPosts = null;
        User = user;
        console.log(User);
        if(user) {
            const userKey = `userData_${user.login}`;
            SavedPosts = localStorage.getItem(userKey); // берем элемент из стораж
        }
        if (SavedPosts) {
            setPosts(JSON.parse(SavedPosts)); // парсим в строку все объекты
            console.log("ЕСТЬ ПОСТЫ "+SavedPosts);
        }
        else {
            setPosts([]);
            console.log("НЕТ ПОСТОВ "+SavedPosts);
        }
    }

    const savePosts = (posts) => {
        console.log(User);
        if(User) {
            const userKey = `userData_${User.login}`;
            localStorage.setItem(userKey, JSON.stringify(posts)); // кладем элемент в стораж
            console.log(User);
        }
        else
            console.log("sjnjsf");
    }

    const createPost = (newPost) => {
        if (newPost.title !== '' && newPost.body !== '') { 
            setPosts([...posts, newPost]) // задаем все элементы
            savePosts([...posts, newPost]); // сохраняем все и последний
            console.log(posts);
        }

    }

    const removePost = (post) => {
        setPosts(posts.filter(p => p.id !== post.id)); // задаем
        savePosts(posts.filter(p => p.id !== post.id)); // сохраняем
    }

    const WrongPassword = () => {
        alert('Неправильный пароль');
    }

    return (
        <div className="App">
            { 
            <Login AccessAllowed={getPosts} AccessRestricted={WrongPassword} onLogin={(user) => setUser(user)}/>
            }
            <PostForm create={createPost} />
            {
                posts.length !== 0
                ?
                <PostList posts={posts} title="Список дел" remove={removePost} />
                :
                <h1 style={{ fontFamily: 'sans-serif', textAlign: 'center' }}>
                    Дел нет
                </h1>
            }
        </div>
    );
}

export default App;
