import React from "react";
import InputLogin from "./UI/login/inputlogin";
import MyButton from "./UI/button/mybutton";
import classes from "./UI/login/InputLogin.module.css"

const Login = ({ userData, onUserDataChange, onSaveUserData }) => {

    const handleInputChange = (event) => {
      const { name, value } = event.target;
      onUserDataChange(name, value);
    };

    const handleLogin = () => {
        const userId = userData.username;
        // Вызываем функцию для сохранения данных в localStorage
        onSaveUserData(userId, userData);
    };

    return (
        <div className={classes.login}>
            <h3>Вход и регистрация</h3>
            <InputLogin
                type="text"
                placeholder='Имя пользователя'
                value={userData.username}
                onChange={handleInputChange}
            />
            <InputLogin 
                type="text"
                placeholder='Пароль'
                value={userData.password}
                onChange={handleInputChange}
            />
            <MyButton onClick={handleLogin}>Вход</MyButton>
            <MyButton>
                Вход
            </MyButton>
        </div>
    )
}

export default Login
