import React from "react";
import {useState} from "react";
import { useEffect } from "react";
import MyButton from "./UI/button/mybutton";
import { Button } from 'react-bootstrap';

const PostItem = (props) => {

    /**-------------------------------- */

    const handleDragStart = (e, draggedItem) => {
        e.dataTransfer.setData('text/plain', draggedItem.id.toString());
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };
    /**-------------------------------- */


    const [completed, setCompleted] = useState(false);

    useEffect(() => {
        for (let j = 0; j < props.posts.length; j++)
            if (props.posts[j].id === props.post.id)
                setCompleted(props.posts[j].completed);
    }, [props.posts])

    useEffect(() => {
        for (let j = 0; j < props.donePosts.length; j++)
            if (props.donePosts[j].id === props.post.id)
                setCompleted(props.donePosts[j].completed);
    }, [props.donePosts])

    const handleCheckbox = () => {
        setCompleted(!completed);

        let key;
        let postsArray;

        for(let i = 0; i < localStorage.length; i++) {
            key = localStorage.key(i);
            const value = localStorage.getItem(key);

            if (key.startsWith('{"login":')) {
                try {
                    if(value.length === 0)
                        continue;
                    postsArray = JSON.parse(value);
                    for(let j = 0; j < postsArray.length; j++) 
                        if(props.post.id === postsArray[j].id) {
                            if(props.flag === true) {
                                const found = props.posts.find(item => item.id === props.post.id);

                                props.remove(found, props.posts, props.donePosts);
                                found.completed = !found.completed;
                                props.create(found, props.donePosts, props.posts, false);
                                
                            }
                            else {
                                const found = props.donePosts.find(item => item.id === props.post.id);

                                props.remove(found, props.donePosts, props.posts);
                                found.completed = !found.completed;
                                props.create(found, props.posts, props.donePosts, false);
                            }
                        }
                }
                catch (error) {
                    console.error('Ошибка при разборе данных:', error);
                }
            }
        }
    }

    return (
        <div className="post" 
            draggable
            onDragStart={(e) => handleDragStart(e, item)}>
            <input type="checkbox" className="chb" onChange={handleCheckbox} checked={completed}/>
            <div className="post__content">
                <strong style={{fontSize:20, fontFamily:'cursive'}}>{props.post.title}</strong>
                <div style={{fontSize:15, fontFamily:'cursive'}}>
                    {props.post.body}
                </div>
            </div>
            <div>
                <MyButton onClick={() => props.remove(props.post, props.flag ? props.posts : props.donePosts,
                    props.flag ? props.donePosts : props.posts)}>
                    Удалить
                </MyButton>
                <Button/>
            </div>
        </div>
    )
}

export default PostItem
