import { useState } from "react"
import ButClass from "../styles/MyButton.module.css"
import classes from "../styles/MyInput.module.css"

const PostForm = ({create}) => {
    
    const [post, setPost] = useState({ title: '', body: '', completed: false})

    const addNewPost = (e) => { // функция добавления дела
        e.preventDefault(); // отменить обновление страницы
        const newPost = {
            ...post, id: Date.now()
        }
        create(newPost);
        setPost({ title: '', body: '', completed: false });
    }

    return (
        <form>
            <input>
                className={classes.NameInput}
                value={post.title}
                onChange={e => setPost({ ...post, title: e.target.value })}
                type="text"
                placeholder='Название'
            </input>
            <button className={ButClass.myBtn} onClick={addNewPost}>Добавить</button>
            <textarea
                className={classes.DescInput}
                value={post.body}
                onChange={e => setPost({ ...post, body: e.target.value })}
                type="text"
                placeholder='Описание'
            />
        </form>
    )
}

export default PostForm
